sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        return;
    }

	g_form.clearOptions('subcategory');
	g_form.addOption('subcategory', '', '-- None --',0);

    if (g_form.getValue('category') == '7' || g_form.getValue('category') == '8') {
        g_form.addOption('subcategory', '', '-- None --');
        g_form.addOption('subcategory', '25', 'Customer Module Demo',1);
        g_form.addOption('subcategory', '26', 'Invoice Module Demo',2);
        g_form.addOption('subcategory', '27', 'Products Module Demo',3);
        g_form.addOption('subcategory', '28', 'Emails Module Demo',4);
        g_form.addOption('subcategory', '29', 'Transactions Module Demo',5);
        g_form.addOption('subcategory', '30', 'Refunds Module Demo',6);
        g_form.addOption('subcategory', '31', 'Coupons Module Demo',7);
        g_form.addOption('subcategory', '32', 'Accounts Module Demo',8);
    } else {
        var choiceFetcher = new GlideAjax('ChoiceFetcher');
        choiceFetcher.addParam('sysparm_name', 'getChoicesWithDependent');
        choiceFetcher.addParam('sysparm_category', g_form.getValue('category'));
        choiceFetcher.getXMLAnswer(function(answer) {
            var data = JSON.parse(answer);
            for (var i = 0; i < data.length; i++) {
                g_form.addOption('subcategory', data[i].value, data[i].label,i+1);
            }


            if (g_form.getValue('category') == '') {
                g_form.clearOptions('subcategory');
                g_form.addOption('subcategory', '', '-- None --');
            }
        });
    }





    //Type appropriate comment here, and begin script below

}
