sys_script_include.script:
var ChoiceFetcher = Class.create();
ChoiceFetcher.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getChoicesWithDependent: function() {
        var category = this.getParameter('sysparm_category');

        var gr = new GlideRecord('sys_choice');
        gr.addQuery('name', 'x_1090111_luco_cas_luco_case');
        gr.addQuery('element', 'subcategory');
        gr.addQuery('dependent_value', category);
        gr.query();

        var choices = [];
        while (gr.next()) {
			var choice = {};
			choice.label = gr.getValue('label');
			choice.value = gr.getValue('value');
			choices.push(choice);
        }
        return JSON.stringify(choices);
    },
    type: 'ChoiceFetcher'
});
