sys_ui_action.script:
var uri = action.getGlideURI();
if(action.getURLParameter("sysparm_view") == "major_case"){
	action.setRedirectURL("sn_customerservice_case.do?sys_id=-1&sysparm_view=default&sysparm_template=major_case_candidate&sysparm_view=default");	
}
else if(gs.hasRole('sn_csm_ocs.ext_agent') && !gs.hasRole('admin')){
		action.setRedirectURL("sn_customerservice_case.do?sys_id=-1&sysparm_query=active=true&sysparm_stack=sn_customerservice_case_list.do?sysparm_query=active=true&sysparm_view=default");
}
else{
	if ((gs.hasRole('sn_customerservice.relationship_agent') || gs.hasRole('sn_esm_location_agent')) && !gs.hasRole('admin') && !gs.hasRole('sn_esm_agent')) {
		// For Paris, order case is not supported for location and relationship agents OOB
		action.setRedirectURL("sn_customerservice_case.do?sys_id=-1&sysparm_query=active=true&sysparm_stack=sn_customerservice_case_list.do?sysparm_query=active=true&sysparm_view=default");
	} else {
		var path = uri.getFileFromPath() + '';
		path = path.substring(0, path.length - 5) + '.do';

		uri.set('sys_id', '-1');
		uri.set('sysparm_view', 'default');
		path = checkWizard(uri, path);
		if (path) {
			action.setRedirectURL(uri.toString(path));
			action.setNoPop(true);
		}
		else
			action.setRedirectURL("sn_customerservice_case.do?sys_id=-1&sysparm_query=active=true&sysparm_view=default");
	}	
}

function checkWizard(uri, path) {
	var already = uri.get('WIZARD:action');

	if (already == 'follow')
		return null;

	var wizID = new GlideappWizardIntercept(path).get();
	if (!wizID)
		return path;

	uri.set('sysparm_parent', wizID);
	uri.set('sysparm_referring_url', uri.toString());
	return 'wizard_view.do';
}
